"use client"

import { useState } from "react"
import { ChevronLeft, ChevronRight } from "lucide-react"
import { Button } from "@/components/ui/button"

const images = [
  {
    src: "data:image/jpeg;base64,",
    alt: "Red Embroidered Kurti"
  },
  {
    src: "data:image/jpeg;base64,",
    alt: "Blue Floral Dress"
  },
  {
    src: "data:image/jpeg;base64,",
    alt: "Classic Abaya"
  }
]

export default function HeroSection() {
  const [current, setCurrent] = useState(0)

  const handlePrev = () => {
    setCurrent((prev) => (prev === 0 ? images.length - 1 : prev - 1))
  }

  const handleNext = () => {
    setCurrent((prev) => (prev === images.length - 1 ? 0 : prev + 1))
  }

  return (
    <section className="relative h-[600px] bg-gradient-to-r from-pink-50 to-blue-50 overflow-hidden">
      {/* Background Pattern */}
      <div className="absolute inset-0 opacity-20">
        <div className="absolute top-10 left-10 w-32 h-32 bg-pink-200 rounded-full blur-xl"></div>
        <div className="absolute top-20 right-20 w-24 h-24 bg-blue-200 rounded-full blur-lg"></div>
        <div className="absolute bottom-20 left-1/4 w-20 h-20 bg-yellow-200 rounded-full blur-lg"></div>
      </div>

      <div className="container mx-auto px-4 h-full flex items-center">
        <div className="flex w-full items-center justify-between">
          {/* Left Content */}
          <div className="flex-1 z-10">
            <div className="text-8xl font-bold mb-4">J.</div>
            <div className="text-xl font-medium mb-2">NEW ARRIVALS</div>
            <p className="text-gray-600 max-w-md">
              Discover our latest collection of premium Islamic fashion and contemporary designs.
            </p>
          </div>

          {/* Right Content - Product Image */}
          <div className="flex-1 relative z-10">
            <div className="bg-white rounded-lg shadow-lg p-4 max-w-sm ml-auto">
              <img
                src={images[current].src}
                alt={images[current].alt}
                className="w-full h-64 object-cover rounded"
              />
            </div>
          </div>
        </div>
      </div>

      {/* Navigation Arrows */}
      <Button
        onClick={handlePrev}
        variant="ghost"
        size="icon"
        className="absolute left-4 top-1/2 transform -translate-y-1/2 bg-white/80 hover:bg-white z-20"
      >
        <ChevronLeft className="w-6 h-6" />
      </Button>
      <Button
        onClick={handleNext}
        variant="ghost"
        size="icon"
        className="absolute right-4 top-1/2 transform -translate-y-1/2 bg-white/80 hover:bg-white z-20"
      >
        <ChevronRight className="w-6 h-6" />
      </Button>

      {/* Dots Indicator */}
      <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 flex space-x-2 z-20">
        {images.map((_, index) => (
          <div
            key={index}
            className={`w-2 h-2 rounded-full ${
              current === index ? "bg-black" : "bg-gray-300"
            }`}
          ></div>
        ))}
      </div>
    </section>
  )
}
